    classLabel = []
    for i in range(lineNum):
        line = lines[i].strip() # trim two side's space
        itemMat = line.split(',')
        resultMat[i,:] = itemMat[0:paraNumber]
        classLabel.append(itemMat[-1])
    Tfile.close()
    return resultMat,classLabel;
