def dataknn(data):
    resultMat=zeros((len(data),4))
    index=0
    classLabel=[]
    for i in range(len(data)):
        resultMat[index,:] = data[i][0:4]
        classLabel.append(data[i][-1])
        index+=1
    return resultMat,classLabel
